def totalJournalists
    puts "Combien y a-t-il de handle dans cette array ?"
    total = @journalist.length
    puts "> #{total}"
end

def smallerHandle
    puts "Quelle est le handle le plus court de cette liste ?"
    smaller_handle=@journalist.sort_by(&:length)[0]
    puts smaller_handle
    puts smaller_handle.length
end

def fiveCharacters
    puts "Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)"
    five_chracters = 0
        @journalist.each do |name|
            journalist_length = name.length
            if journalist_length == 6
                five_chracters += 1  
            end
        end
    puts five_chracters
    end

def firstCharacter_upper
    puts "Combien commencent par une majuscule (première lettre juste après le @) ?"
    first_character_upper = 0
    @journalist.each do |name|
        character = name[1]
        if character == character.upcase
            first_character_upper += 1
        end
    end
    puts first_character_upper
end

def alphabeticalOrder 
    puts "Cette fonction trie les noms des journalists par ordre alphabetique"
    puts @journalist.map(&:downcase).sort 
end

def sizeOrder
    puts "Cette fonction trie les noms des journalists par le nombre des charcters"
    puts @journalist.sort_by(&:length) 
end

def whereIs_epenser
    puts "Quelle est la position dans l'array de la personne @epenser ?"
    element = "@epenser"
    index = @journalist.index(element)
    puts "@epenser se trouve dans la position #{index + 1}"
end

def biggerHandle
    puts "Quelle est le handle le plus court de cette liste ?"
    bigger_handle=@journalist.sort_by(&:length)[-1]
    puts bigger_handle
    puts bigger_handle.length
end

